package if_else;

public class vidu3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Viết chương trình kiểm tra xem a có chia hết cho b hay k
		int a = 10;
		int b = 5;
		if (a % b == 0) {
			System.out.print("a chia het cho b");
		} else {
			System.out.print("a khong chia het cho b");
		}
	}

}
